# Function to check if a patient ID is in the list
contains_id() {
    local id="$1"
    for pid in "${patient_ids[@]}"; do
        if [ "$pid" == "$id" ]; then
            return 0
        fi
    done
    return 1
}

# Loop through all .json files in the specified folder
for file in "$json_folder"/*.json; do
  # Get the filename without the extension
  patient_id="$(basename "$file" .json)"
  
  # Check if the patient ID is in the list
  if ! contains_id "$patient_id"; then
    continue
  fi
  
  # Print the current file being processed (optional)
  echo "Processing file: $file with patient_id: $patient_id"
  
  # Execute the curl command with the patient_id and file
  curl --location --request POST "http://prism.prod.ontada/prism/trialfinder/api/v2/patients/${patient_id}/jobs/nlp-l1/callback" \
       --header 'triomics-tenant-id: 718bdaf0-f020-4486-8cf7-785b7ddf1f7e' \
       --header 'triomics-member-id: SYSTEM_USER' \
       --header 'Content-Type: application/json' \
       --data-binary "@${file}"
done